    const coin = view.getOutput(prevout);
    assert(coin);

    if (coin.value < 0 || coin.value > consensus.MAX_MONEY)
      return [-1, 'bad-txns-inputvalues-outofrange', 100];

    total += coin.value;

    if (total < 0 || total > consensus.MAX_MONEY)
      return [-1, 'bad-txns-inputvalues-outofrange', 100];
